    {% for article in articles %}
    

    <article>
        <h2>{{ article.title }}</h2>
        <div class=\"metadata\">Ecrit le {{ article.createdAt | date('d/m/Y') }} à {{ article.createdAt |date('h:i')}} dans la categorie politique</div>
        <div class=\"content\">
           <img src=\"{{ article.image }}\" alt=\"\">

            {{ article.content | raw }}

            <a href=\"{{path ('blog_show',{'id':article.id})}}\" class=\"btn btn-primary\">lire la suite</a>
        </div>
    </article>

    {% endfor %}
</section>

{% endblock %}
